  `ChiTiet` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `AnhChinh` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `ListAnh` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
