INSERT INTO `product` (`MaSP`, `CategoryID`, `TenSp`, `Slug`, `DonViTinh`, `XuatXu`, `MoTa`, `ChiTiet`, `AnhChinh`, `ListAnh`, `NoiBat`, `TrangThai`, `NgayTao`) VALUES
('000000000001', 18, ' Táo Trucape Nam Phi', 'tao-trucape-nam-phi', 'Kg', ' Nam Phi', '\r\n\r\nCAM KẾT\r\nHoa quả tươi sạch\r\nKhông chất bảo quản\r\nDịch vụ chuyên nghiệp\r\nKhông thuốc biến đổi gien\r\nKhông hàng Trung Quốc\r\nGiá cả cạnh tranh\r\nSẢN PHẨM BÁN CHẠY\r\nTáo Envy New Zealand\r\nTáo Envy New Zealand\r\nXem ngay\r\nLê Hàn Quốc\r\nLê Hàn Quốc\r\nXem ngay\r\nN', '<h1>T&aacute;o Trucape Nam Phi</h1>\r\n\r\n<h3>1.Giống v&agrave; chủng loại</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ndash; Nhập khẩu: Nam Phi.</p>\r\n\r\n<h3>2. Đặc điểm sản phẩm</h3>\r\n\r\n<p>&ndash; K&iacute;ch thước quả kh&aacute; nhỏ, nhưng cầm rất chắc tay. T&aacute;o gi&ograve;n, ngọt v&agrave; nhiều nước, hơn nữa quả lại kh&ocirc;ng qu&aacute; to l&agrave; đặc điểm rất được y&ecirc;u th&iacute;ch ở d&ograve;ng t&aacute;o n&agrave;y.</p>\r\n\r\n<h3>3. Gi&aacute; trị dinh dưỡng</h3>\r\n\r\n<ul>\r\n	<li>Giảm cholesterol xấu: T&aacute;o c&oacute; chứa pectin, một loại chất xơ h&ograve;a tan. Pectin c&oacute; hiệu quả trong việc l&agrave;m giảm cholesterol xấu trong cơ thể bằng c&aacute;ch giảm lipoprotein mật độ thấp (LDL) gi&uacute;p l&agrave;m giảm nguy cơ mắc bệnh xơ vữa động mạch v&agrave; bệnh tim hiệu quả.</li>\r\n	<li>Ngăn ngừa bệnh Alzheimer: T&aacute;o c&oacute; t&aacute;c dụng ngăn ngừa bệnh Alzheimer l&agrave; bởi v&igrave; trong t&aacute;o c&oacute; chứa một chất chống oxy h&oacute;a hiệu quả gọi l&agrave; quercetin.<br />\r\n	Quercetin đặc biệt c&oacute; hiệu ứng với hệ thần kinh, đ&oacute; l&agrave; bảo vệ c&aacute;c tế b&agrave;o n&atilde;o khỏi bị hư hại, ph&ograve;ng tr&aacute;nh c&aacute;c bệnh li&ecirc;n quan đến thần kinh, bao gồm cả Alzheimer.</li>\r\n	<li>Giảm nguy cơ tiểu đường: Ti&ecirc;u thụ t&aacute;o ở mức độ vừa phải c&ograve;n c&oacute; lợi ở chỗ giữ cho lượng đường trong m&aacute;u của bạn lu&ocirc;n ở mức kiểm so&aacute;t, tr&aacute;nh tăng cao dẫn đến bệnh tiểu đường. C&aacute;c dưỡng chất thực vật v&agrave; chất chống oxy h&oacute;a trong t&aacute;o như polyphenol sẽ l&agrave;m giảm hấp thu glucose v&agrave; ổn định lượng đường trong m&aacute;u.</li>\r\n	<li>Tăng cường hệ thống miễn dịch của cơ thể: Vitamin C l&agrave; một trong những th&agrave;nh phần c&oacute; t&aacute;c dụng hỗ trợ hệ miễn dịch của con người. Vitamin C lại c&oacute; nhiều trong t&aacute;o n&ecirc;n ti&ecirc;u thụ t&aacute;o h&agrave;ng ng&agrave;y sẽ gi&uacute;p cơ thể trước những sự tấn c&ocirc;ng của c&aacute;c yếu tố b&ecirc;n ngo&agrave;i.</li>\r\n	<li>Ngăn ngừa đục thủy tinh thể: Một trong những l&yacute; do ch&iacute;nh của t&igrave;nh trạng đục thủy tinh thể l&agrave; bởi sự tho&aacute;i h&oacute;a do oxy h&oacute;a g&acirc;y ra.<br />\r\n	Thường xuy&ecirc;n ăn t&aacute;o sẽ gi&uacute;p loại bỏ c&aacute;c gốc tự do, đẩy l&ugrave;i nguy cơ oxy h&oacute;a dẫn đến tho&aacute;i h&oacute;a v&agrave; do đ&oacute; bảo vệ mắt khỏi nguy cơ đục thủy tinh thể.</li>\r\n	<li>Tốt cho da: Collagen v&agrave; elastin trong t&aacute;o sẽ c&oacute; t&aacute;c động l&ecirc;n l&agrave;n da của bạn để giữ cho da trắng s&aacute;ng, rạng rỡ v&agrave; khỏe mạnh hơn. Ngo&agrave;i ra, c&aacute;c chất chống oxy h&oacute;a trong t&aacute;o cũng g&oacute;p phần đẩy l&ugrave;i qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a, đồng thời tăng sức đề kh&aacute;ng của da.</li>\r\n	<li>Giảm c&acirc;n: Sự hiện diện của chất chống oxy h&oacute;a v&agrave; pectin trong t&aacute;o tươi ch&iacute;nh l&agrave; yếu tố gi&uacute;p cho trọng lượng cơ thể của bạn lu&ocirc;n ở mức kiểm so&aacute;t, ổn định.<br />\r\n	Polyphenol trong t&aacute;o cũng gi&uacute;p điều h&ograve;a sự chuyển h&oacute;a chất b&eacute;o n&ecirc;n bạn sẽ kh&ocirc;ng phải lo đến nguy cơ tăng c&acirc;n.</li>\r\n</ul>\r\n\r\n<h3>4. Lưu &yacute; bảo quản</h3>\r\n\r\n<p>&ndash; Để t&aacute;o kh&ocirc;ng bị th&acirc;m, ta cắt t&aacute;o nh&uacute;ng qua nước lạnh c&oacute; pha vai giọt nước cốt chanh hoặc nước muối lo&atilde;ng.<br />\r\n&ndash; Nhiệt độ bảo quản: Bảo quản tại nh&agrave;: Tủ lạnh từ 4 đến 8 độ C. T&aacute;o giữ đươc độ tươi, độ gi&ograve;n trong v&ograve;ng 4 tuần. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, độ PSI thấp hơn (t&aacute;o xốp hơn). Cần tr&aacute;nh để với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ nhiễm m&ugrave;i.</p>\r\n\r\n<h3>&nbsp;</h3>\r\n', 'taotrucape_41.jpg', 'taotrucape.jpg,taotrucape2.jpg', 1, 1, '2021-04-22 23:26:08'),
('000000000002', 18, ' Táo hữu cơ Juliet', 'tao-huu-co-juliet', 'Kg', 'Pháp', '– Giảm cholesterol và chất chống oxy hóa, ngăn ngừa bệnh tim.\r\n– Điều chỉnh áp suất máu và giữ cho nhịp đập tim ở mức ổn định, chất chống ôxy hoá tự nhiên giúp bảo vệ thành mạch máu.\r\n– Vỏ táo giàu chất xơ và có lợi cho hệ tiêu hóa\r\n– Lượng đường tự nhiên', '<h1><strong>T&aacute;o hữu cơ Juliet</strong></h1>\r\n\r\n<h3>1.Giống v&agrave; chủng loại</h3>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/Tao-Apple/tao-juliet-01-min1.jpg\" /></p>\r\n\r\n<p>&ndash; Nhập khẩu từ Ph&aacute;p<br />\r\n&ndash; T&aacute;o Juliet l&agrave; thương hiệu t&aacute;o nổi tiếng bậc nhất thế giới, c&oacute; thể coi đ&acirc;y l&agrave; loại t&aacute;o hữu cơ đầu ti&ecirc;n đạt chất lượng xuất khẩu. Ra đời ở những trang trại t&aacute;o rộng lớn Nam nước Ph&aacute;p, diện t&iacute;ch dự kiến 350 hecta. T&aacute;o Juliet Organic l&agrave; th&agrave;nh quả của qu&aacute; tr&igrave;nh nghi&ecirc;n cứu l&acirc;u năm (những c&acirc;y đầu ti&ecirc;n được chồng từ những năm 90 của thế kỉ trước), qua nhiều thử nghiệm nghi&ecirc;m ngặt, t&aacute;o juliet kh&ocirc;ng sử bất k&igrave; loại ph&acirc;n b&oacute;n, h&oacute;a chất, thuốc trừ s&acirc;u diệt cỏ n&agrave;o, nghi&ecirc;m cấm c&aacute;c loại thuốc k&iacute;ch th&iacute;ch tăng trưởng. Đảm bảo t&aacute;o hữu cơ 100%.</p>\r\n\r\n<p>- M&ugrave;a vụ: T&aacute;o Juliet thường c&oacute; quanh năm, đứt lứa 1-2 th&aacute;ng do thời tiết</p>\r\n\r\n<h3>2. Đặc điểm sản phẩm</h3>\r\n\r\n<p>&ndash; T&aacute;o hữu cơ Juliet c&oacute; k&iacute;ch thước quả vừa phải. Vỏ t&aacute;o m&agrave;u đỏ sậm tr&ecirc;n nền v&agrave;ng nhạt. Thịt t&aacute;o m&agrave;u trắng, độ gi&ograve;n, ngọt v&agrave; điều tạo n&ecirc;n sự kh&aacute;c biệt với c&aacute;c giống t&aacute;o kh&aacute;c l&agrave; hương thơm đậm của n&oacute;.<br />\r\n&ndash; C&acirc;y t&aacute;o Juliet c&oacute; đặc t&iacute;nh rất khỏe mạnh, c&oacute; khả năng tự nhi&ecirc;n chống lại một số c&aacute;c căn bệnh thường gặp ở t&aacute;o.<br />\r\n&ndash; T&aacute;o Juliet l&agrave; giống t&aacute;o đầu ti&ecirc;n tr&ecirc;n thế giới được sản xuất độc quyền sử dụng c&aacute;c biện ph&aacute;p trồng trọt hữu cơ.<br />\r\n&ndash; Tại Ph&aacute;p, t&aacute;o hữu cơ Juliet thực sự rất phổ biến v&agrave; được ưa chuộng. N&oacute; thậm ch&iacute; c&oacute; cả nh&acirc;n vật hoạt h&igrave;nh l&agrave;m biểu tượng ri&ecirc;ng m&agrave; chưa giống t&aacute;o n&agrave;o c&oacute;.</p>\r\n\r\n<h3>3. Gi&aacute; trị dinh dưỡng</h3>\r\n\r\n<p>&ndash; Giảm cholesterol v&agrave; chất chống oxy h&oacute;a, ngăn ngừa bệnh tim.<br />\r\n&ndash; Điều chỉnh &aacute;p suất m&aacute;u v&agrave; giữ cho nhịp đập tim ở mức ổn định, chất chống &ocirc;xy ho&aacute; tự nhi&ecirc;n gi&uacute;p bảo vệ th&agrave;nh mạch m&aacute;u.<br />\r\n&ndash; Vỏ t&aacute;o gi&agrave;u chất xơ v&agrave; c&oacute; lợi cho hệ ti&ecirc;u h&oacute;a<br />\r\n&ndash; Lượng đường tự nhi&ecirc;n trong t&aacute;o kh&ocirc;ng ảnh hưởng tới bệnh nh&acirc;n tiểu đường.<br />\r\n&ndash; Tăng khả năng ghi nhớ của n&atilde;o người, n&acirc;ng cao khả năng đề kh&aacute;ng của cơ thể.</p>\r\n\r\n<h3>4. Lưu &yacute; bảo quản</h3>\r\n\r\n<p>&ndash; Để t&aacute;o kh&ocirc;ng bị th&acirc;m, ta cắt t&aacute;o nh&uacute;ng qua nước lạnh c&oacute; pha vai giọt nước cốt chanh hoặc nước muối lo&atilde;ng.<br />\r\n&ndash; Nhiệt độ bảo quản: Bảo quản tại nh&agrave;: Tủ lạnh từ 4 đến 8 độ C. T&aacute;o giữ đươc độ tươi, độ gi&ograve;n trong v&ograve;ng 4 tuần. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, độ PSI thấp hơn (t&aacute;o xốp hơn). Cần tr&aacute;nh để với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ nhiễm m&ugrave;i.</p>\r\n', 'juliet1_17.jpg', 'juliet1.jpg', 1, 1, '2021-04-22 23:33:15'),
('000000000003', 22, 'Lê má hồng Bỉ', 'le-ma-hong-bi', 'Kg', ' Bỉ', '– Quả lê má hồng Bỉ cũng giống như các loại lê khác, tính lạnh,  bình  hòa không độc hại, rất giàu vitaminC và pectin là chất giúp làm  tăng độ  xốp và men vi sinh giúp hệ tiêu hóa ổn định, hỗ trợ thải độc tố  trong  cơ thể, ngăn ngừa ung thư hệ tiêu hóa ', '<h1><strong>L&Ecirc; M&Aacute; HỒNG BỈ</strong></h1>\r\n\r\n<h2>1. Giống v&agrave; chủng loại</h2>\r\n\r\n<p>- Nhập khẩu từ Bỉ</p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>&ndash; Vỏ ngo&agrave;i rất dễ nhận biết v&igrave; c&oacute; ba m&agrave;u đặc trưng xanh, đỏ, v&agrave;ng xen kẽ &nbsp;nhau rất đẹp. H&igrave;nh d&aacute;ng ph&iacute;a tr&ecirc;n thon d&agrave;i, ph&iacute;a dưới hơi bầu tr&ograve;n &nbsp;giống giọt nước. L&ecirc; m&aacute; hồng Bỉ c&oacute; vị ngọt dịu, rất gi&ograve;n v&agrave; thơm, ngon hơn &nbsp;khi ăn lạnh v&agrave;o thời tiết n&oacute;ng bức, được người ti&ecirc;u d&ugrave;ng tr&ecirc;n to&agrave;n thế &nbsp;giới ưa chuộng.</p>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/le-bi-1.jpg\" /></p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>&ndash; Quả l&ecirc; m&aacute; hồng Bỉ cũng giống như c&aacute;c loại l&ecirc; kh&aacute;c, t&iacute;nh lạnh, &nbsp;b&igrave;nh h&ograve;a kh&ocirc;ng độc hại, rất gi&agrave;u vitaminC v&agrave; pectin l&agrave; chất gi&uacute;p l&agrave;m &nbsp;tăng độ xốp v&agrave; men vi sinh gi&uacute;p hệ ti&ecirc;u h&oacute;a ổn định, hỗ trợ thải độc tố &nbsp;trong cơ thể, ngăn ngừa ung thư hệ ti&ecirc;u h&oacute;a v&agrave; giảm cholesterol xấu &nbsp;trong m&aacute;u.<br />\r\nNgo&agrave;i ra, L&ecirc; c&ograve;n chứa vitamin A,B,C,D v&agrave; E. Một tr&aacute;i l&ecirc; c&oacute; thể cung &nbsp;cấp 10% h&agrave;m lượng vitamin C v&agrave; h&agrave;m lượng canxi kh&aacute; lớn gi&uacute;p tăng cường &nbsp;hệ miễn dịch, l&agrave;m đẹp da, chắc xương.<br />\r\n&ndash; Vỏ quả l&ecirc; c&oacute; gi&aacute; trị chữa bệnh cao, nhiều chất xơ v&agrave; Kali n&ecirc;n rất c&oacute; lợi cho tim, phổi, gi&uacute;p ti&ecirc;u độc hạ nhiệt.<br />\r\n&ndash; L&ecirc; mặc d&ugrave; rất ngọt, song độ n&oacute;ng v&agrave; độ ngọt g&acirc;y b&eacute;o rất thấp, rất th&iacute;ch hợp với những người th&iacute;ch ăn ngọt nhưng sợ tăng c&acirc;n.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản</h2>\r\n\r\n<p>&ndash; L&ecirc; m&aacute; hồng Bỉ được bảo quản tốt nhất trong hộp nhựa k&iacute;n ở &nbsp;nhiệt độ 0 &ndash; 4oC. Nếu để ở nhiệt độ thường (25 độ C), L&ecirc; sẽ ch&iacute;n sau 1- 3 &nbsp;ng&agrave;y.<br />\r\n&ndash; Vỏ L&ecirc; rất dễ bị bầm dập, cần nhẹ tay khi vận chuyển xa . Tốt nhất &nbsp;n&ecirc;n để L&ecirc; trong th&ugrave;ng xốp v&agrave; đảm bảo quả kh&ocirc;ng bị va đập, dẫn đến dập &nbsp;v&agrave; th&acirc;m b&ecirc;n trong.<br />\r\n&ndash; L&ecirc; m&aacute; hồng Bỉ c&oacute; yếu điểm nhất l&agrave; ở cuống. Nếu L&ecirc; bị ướt &nbsp;hoặc bảo quản kh&ocirc;ng đảm bảo nhiệt độ, cuống l&ecirc; c&oacute; thể bị đọng nước v&agrave; bị &nbsp;vi khuẩn tấn c&ocirc;ng. Khi đ&oacute;, L&ecirc; sẽ bị th&acirc;m ở b&ecirc;n trong, trong khi b&ecirc;n &nbsp;ngo&agrave;i nh&igrave;n ho&agrave;n to&agrave;n b&igrave;nh thường. Nếu gặp trường hợp n&agrave;y, h&atilde;y đem tới &nbsp;cửa h&agrave;ng đ&atilde; mua để đổi sang quả kh&aacute;c.<br />\r\n&ndash; L&ecirc; m&aacute; hồng Bỉ đ&atilde; ch&iacute;n n&ecirc;n được bảo quản lạnh v&agrave; ăn m&aacute;t.<br />\r\n&ndash; Chỉ n&ecirc;n rửa đ&uacute;ng số lượng cần d&ugrave;ng v&igrave; khi L&ecirc; m&aacute; hồng Bỉ đ&atilde; &nbsp;rửa kh&ocirc;ng d&ugrave;ng hết vi khuẩn dễ x&acirc;m nhập l&agrave;m hỏng tr&aacute;i c&acirc;y do đ&atilde; mất đi &nbsp;lớp phấn bảo về tự nhi&ecirc;n bao quanh quả.</p>\r\n', '7607044394775fc8dd28f0d6731fd22b-6-43-420-420-1543910851_59.jpg', '7607044394775fc8dd28f0d6731fd22b-6-43-420-420-1543910851.jpg', 1, 1, '2021-04-22 23:56:25'),
('000000000004', 22, 'Lê thiên đường Úc', 'le-thien-duong-uc', 'Kg', 'Australia', 'Là loại lê duy nhất có quả trái mùa, lê thiên đường có gì đặc biệt?!', '<h2><strong>L&Ecirc; THI&Ecirc;N ĐƯỜNG &Uacute;C</strong></h2>\r\n\r\n<h2>1. Giống v&agrave; chủng loại</h2>\r\n\r\n<p>- Xuất xứ: Australia</p>\r\n\r\n<p>L&agrave; loại l&ecirc; duy nhất tại Australia cho quả tr&aacute;i m&ugrave;a. L&ecirc; thi&ecirc;n đường thường được thu hoạch khoảng từ th&aacute;ng 12 đến th&aacute;ng 2 năm sau.</p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>L&ecirc; Thi&ecirc;n Đường quả nhỏ c&oacute; h&igrave;nh d&aacute;ng gần giống quả sung, vỏ mỏng, ăn c&oacute; &nbsp;vị ngọt m&aacute;t, gi&ograve;n, đặc biệt l&agrave; vỏ quả mỏng v&agrave; mềm n&ecirc;n khi ăn chỉ cần rửa &nbsp;sạch l&agrave; ăn ngay (ăn cả vỏ).</p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>- Quả L&ecirc; Thi&ecirc;n đường rất gi&agrave;u vitamin C v&agrave; pectin l&agrave; chất gi&uacute;p l&agrave;m tăng độ &nbsp;xốp v&agrave; men vi sinh gi&uacute;p hệ ti&ecirc;u h&oacute;a ổn định, gi&uacute;p nhu động ruột thải &nbsp;c&aacute;c chất cặn b&atilde; dễ d&agrave;ng.</p>\r\n\r\n<p>- Vỏ quả l&ecirc; c&oacute; gi&aacute; trị chữa bệnh cao, c&oacute; lợi cho tim v&agrave; phổi, gi&uacute;p ti&ecirc;u &nbsp;độc hạ nhiệt. Một tr&aacute;i l&ecirc; c&oacute; thể cung cấp 10% h&agrave;m lượng vitamin C v&agrave; h&agrave;m &nbsp;lượng Canxi kh&aacute; lớn.</p>\r\n\r\n<p>- L&ecirc; Thi&ecirc;n Đường mặc d&ugrave; ăn rất ngọt , song độ n&oacute;ng v&agrave; độ ngọt g&acirc;y b&eacute;o lại &nbsp;rất thấp v&igrave; vậy rất th&iacute;ch hợp với những ai th&iacute;ch ăn ngọt nhưng sợ tăng &nbsp;c&acirc;n.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản</h2>\r\n\r\n<p>L&ecirc; Thi&ecirc;n đường được bảo &nbsp;quản tốt nhất trong nhiệt độ 0 - 4 độ C, nếu để L&ecirc; ở nhiệt độ thường ( &nbsp;25 độ C) L&ecirc; sẽ ch&iacute;n sau 1- 3 ng&agrave;y.</p>\r\n', '27332403_10215732134553778_4033669892848475190_n_51.jpg', '27332403_10215732134553778_4033669892848475190_n.jpg', 1, 1, '2021-04-22 23:58:00'),
('000000000005', 20, 'Cam đỏ Mỹ', 'cam-do-my', 'Kg', 'My', '– Tốt cho làn da, giúp chống lão hóa, có tác dụng phục hồi sức khỏe.\r\n– Giúp bảo vệ tim mạch, tiêu diệt các mô gây ung thư trong cơ thể.\r\n– Tốt cho mắt, có lợi cho quá trình tiêu hoá.', '<h1><strong>CAM ĐỎ MỸ</strong></h1>\r\n\r\n<h2>1. Giống v&agrave; chủng loại</h2>\r\n\r\n<p>&ndash; Nhập khẩu Mỹ.</p>\r\n\r\n<p>- M&ugrave;a vụ: Từ th&aacute;ng 1 đến th&aacute;ng 6 h&agrave;ng năm, dao động theo thời tiết</p>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/2020-04-24-cam-do-my-thiet-ke-23-min.jpg\" /></p>\r\n\r\n<h3>2. Đặc điểm sản phẩm</h3>\r\n\r\n<p>&ndash; L&agrave; giống cam kh&ocirc;ng hạt, thịt quả c&oacute; m&agrave;u đỏ, t&eacute;p to, mọng nước, vị ngọt thanh m&aacute;t, rất thơm.</p>\r\n\r\n<h3>3. Gi&aacute; trị dinh dưỡng</h3>\r\n\r\n<p>&ndash; Tốt cho l&agrave;n da, gi&uacute;p chống l&atilde;o h&oacute;a, c&oacute; t&aacute;c dụng phục hồi sức khỏe.<br />\r\n&ndash; Gi&uacute;p bảo vệ tim mạch, ti&ecirc;u diệt c&aacute;c m&ocirc; g&acirc;y ung thư trong cơ thể.<br />\r\n&ndash; Tốt cho mắt, c&oacute; lợi cho qu&aacute; tr&igrave;nh ti&ecirc;u ho&aacute;.</p>\r\n\r\n<h3>4. Lưu &yacute; bảo quản</h3>\r\n\r\n<p>Cam c&oacute; thể được bảo quản ở nhiệt độ thường hoặc trong tủ lạnh. Nếu bảo quản trong tủ lạnh, nhiệt độ th&iacute;ch hợp nhất từ 7 &ndash; 10 độ C. Đối với cả hai phương ph&aacute;p bảo quản, cam thường sẽ tươi được trong khoảng 2 tuần v&agrave; sẽ giữ được độ tươi c&ugrave;ng h&agrave;m lượng vitamin.<br />\r\n&ndash; C&aacute;ch tốt nhất để bảo quản cam l&agrave; để tự nhi&ecirc;n thay v&igrave; cho v&agrave;o t&uacute;i nhựa v&igrave; nếu tiếp x&uacute;c với độ ẩm, cam c&oacute; thể dễ d&agrave;ng xuất hiện nấm mốc.<br />\r\n&ndash; Cam ruột đỏ Mỹ vị ngọt thanh, &iacute;t xơ thường d&ugrave;ng để ăn tươi chứ kh&ocirc;ng n&ecirc;n &eacute;p nước.</p>\r\n', '27332403_10215732134553778_4033669892848475190_n_21.jpg', '27332403_10215732134553778_4033669892848475190_n_52.jpg', 1, 1, '2021-04-23 00:00:20'),
('000000000006', 20, 'Cam xoàn Lai Vung', 'cam-xoan-lai-vung', 'Kg', 'Việt Nam', '- Cam xoàn hay còn gọi là Cam đường, là những trái cam có vòng xoáy  như đồng tiền bên dưới trái, vỏ mỏng, chắc múi, tép cam màu vàng nhạt,  đặc biệt là vị ngọt thanh, có mùi thơm đặc trưng của Cam Việt Nam. \r\n\r\n-  Trọng lượng trung bình 150 – 250gram/trá', '<h1><strong>CAM XO&Agrave;N LAI VUNG</strong></h1>\r\n\r\n<h2>1. Giống v&agrave; chủng loại:</h2>\r\n\r\n<p>- Xuất xứ: Lai Vung, Đồng Th&aacute;p</p>\r\n\r\n<p>- M&ugrave;a vụ: cam xo&agrave;n c&oacute; quanh năm, c&oacute; thể bị đứt lứa do thời tiết</p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/cam-xoan-01-10-min.jpg\" /></p>\r\n\r\n<p>- Cam xo&agrave;n hay c&ograve;n gọi l&agrave; Cam đường, l&agrave; những tr&aacute;i cam c&oacute; v&ograve;ng xo&aacute;y như đồng tiền b&ecirc;n dưới tr&aacute;i, vỏ mỏng, chắc m&uacute;i, t&eacute;p cam m&agrave;u v&agrave;ng nhạt, đặc biệt l&agrave; vị ngọt thanh, c&oacute; m&ugrave;i thơm đặc trưng của Cam Việt Nam.&nbsp;</p>\r\n\r\n<p>- Trọng lượng trung b&igrave;nh 150 &ndash; 250gram/tr&aacute;i. Cam vừa ch&iacute;n tới c&oacute; vỏ m&agrave;u v&agrave;ng xanh nhưng nếu ch&iacute;n đ&uacute;ng l&uacute;c th&igrave; vỏ chuyển sang m&agrave;u v&agrave;ng.</p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>&nbsp;Trung b&igrave;nh với mỗi 100gr quả cam chứa 87,6g nước, 1.104 microgram Carotene &ndash; một loại vitamin chống oxy h&oacute;a, 30mg vitamin C, 10,9g chất tinh bột, 93mg kali, 26mg canxi, 9mg Magnesium, 0,3g chất xơ, 4,5mg natri, 7mg Chromium, 20mg phốt pho, 0,32mg sắt v&agrave; gi&aacute; trị năng lượng l&agrave; 48 Kcal.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản</h2>\r\n\r\n<p>Trong tủ m&aacute;t 5 độ C, để v&agrave;o th&ugrave;ng xốp rồi sau đ&oacute; cho v&agrave;o ngăn m&aacute;t<br />\r\n<br />\r\nThời gian : Hơn 1 tuần. Kh&ocirc;ng n&ecirc;n để l&acirc;u, cam sẽ bị h&eacute;o v&agrave; mất chất dinh dưỡng.</p>\r\n', 'cam_xoan_3_22.jpg', 'cam_xoan_3.jpg', 1, 1, '2021-04-23 00:01:47'),
('000000000007', 21, 'Nho Đen Midnight Mỹ', 'nho-den-midnight-my', 'Kg', ' Mỹ', 'Nho đen không hạt có hình tròn thon, màu đen sẫm, vỏ mỏng, còn nguyên cuống và phấn. Ăn rất ngọt nhưng vẫn có vị thanh mát. Đặc biệt là nho không có hạt, thích hợp cho trẻ ăn.', '<h1><strong>NHO ĐEN MIDNIGHT MỸ</strong></h1>\r\n\r\n<h2>1. Giống v&agrave; chủng loại</h2>\r\n\r\n<p>- Xuất xứ : Mỹ</p>\r\n\r\n<p>- Nho đen kh&ocirc;ng hạt Mỹ c&oacute; xuất xứ từ bang California, Oregon, Washington - nơi c&oacute; kh&iacute; hậu ấm &aacute;p v&agrave; kh&ocirc;.</p>\r\n\r\n<p>- Nho đen Mỹ đ&atilde; khẳng định được chất lượng tr&ecirc;n to&agrave;n cầu với những quả nho căng mọng,da mịn m&agrave;ng nh&igrave;n rất sang trọng, lịch sự.</p>\r\n\r\n<p>- M&ugrave;a vụ: Từ th&aacute;ng 6 đến th&aacute;ng 12, c&oacute; dao động do thời tiết&nbsp;</p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>Nho đen kh&ocirc;ng hạt&nbsp;c&oacute; h&igrave;nh tr&ograve;n thon, m&agrave;u đen sẫm, vỏ mỏng, c&ograve;n nguy&ecirc;n cuống v&agrave; phấn. Ăn rất ngọt nhưng vẫn c&oacute; vị thanh m&aacute;t. Đặc biệt l&agrave; nho kh&ocirc;ng c&oacute; hạt, th&iacute;ch hợp cho trẻ ăn.</p>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/Nho-grapes/nho-den-my-1.jpg\" /></p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>- Đ&acirc;y l&agrave; loại hoa quả rất gi&agrave;u dinh dưỡng, c&oacute; t&aacute;c dụng bổ t&igrave;, &iacute;ch kh&iacute;, gi&uacute;p bạn c&oacute; một cơ thể khỏe mạnh, hệ miễn dịch tốt v&agrave; l&agrave;m chậm qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a</p>\r\n\r\n<p>- Canxi, kali, photpho, sắt, vitamin B1, B2, B6, C v&agrave; c&aacute;c loại axit amin cần thiết tốt cho người suy nhược thần kinh v&agrave; c&oacute; lợi cho ti&ecirc;u h&oacute;a</p>\r\n\r\n<p>- Hợp chất Resveratrol trong nho đen kh&ocirc;ng hạt Mỹ đặc biệt c&oacute; trong vỏ l&agrave; phần gi&uacute;p giải độc tốt, giảm m&aacute;u nhiễm mỡ, chống tụ huyết, ph&ograve;ng chống xơ vỡ động mạch v&agrave; tăng cường hệ thống miễn dịch của cơ thể. V&igrave; thế khi ăn bạn chỉ n&ecirc;n rửa sạch rồi ăn trực tiếp.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản</h2>\r\n\r\n<p>&ndash;&nbsp;&nbsp;C&aacute;ch bảo quản nho tốt nhất: để Nho chưa rửa v&agrave;o trong tủ lạnh (ở nhiệt độ từ 0 &ndash; 4 độ C) để kh&ocirc;ng l&agrave;m đi lớp phấn phủ tự nhi&ecirc;n của quả nho. Chỉ &nbsp;n&ecirc;n rửa nho trước khi ăn tươi hoặc chế biến m&oacute;n ăn.</p>\r\n', 'Nho-den-My_18.jpg', 'Nho-den-My.jpg', 1, 1, '2021-04-23 00:04:06'),
('000000000008', 21, 'Nho rượu Nhật Bản', 'nho-ruou-nhat-ban', 'Kg', ' Nhật Bản', 'Trong quá trình nuôi trồng nho rượu Kyoho, cứ 100 chùm nho người ta lại  tỉa đi 90 chùm và chỉ để lại duy nhất 10 chùm có chất lượng cao nhất. Vì  vậy quả nho rượu Kyoho to tròn, thịt rất thơm, ngọt, mềm, có từ 1 đến 2  hạt và rất dễ lột vỏ. Điểm đặc biệt', '<h1><strong>Nho rượu Nhật Bản</strong></h1>\r\n\r\n<h3>1. Giống v&agrave; chủng loại</h3>\r\n\r\n<p>Nho đen Kyoho l&agrave; giống nho cao cấp của Nhật Bản, l&agrave; giống lai ch&eacute;o giữa &nbsp;giống nho Mỹ Ishiharawase v&agrave; giống nho Ch&acirc;u &Acirc;u Centennial.</p>\r\n\r\n<p>Kyoho nghĩa l&agrave; &quot;vĩ đại&quot;, &quot;ngọn n&uacute;i lớn&quot; cũng như niềm tự h&agrave;o đến từ &nbsp;Nhật Bản, với những sản phẩm cao cấp, chất lượng như nho rượu Kyoho.</p>\r\n\r\n<h3>2. Đặc điểm sản phẩm</h3>\r\n\r\n<p>- Trong &nbsp;qu&aacute; tr&igrave;nh nu&ocirc;i trồng nho rượu Kyoho, cứ 100 ch&ugrave;m nho người ta lại tỉa &nbsp;đi 90 ch&ugrave;m v&agrave; chỉ để lại duy nhất 10 ch&ugrave;m c&oacute; chất lượng cao nhất. V&igrave; vậy &nbsp;quả nho rượu Kyoho to tr&ograve;n, thịt rất thơm, ngọt, mềm, c&oacute; từ 1 đến 2 hạt &nbsp;v&agrave; rất dễ lột vỏ. Điểm đặc biệt nhất của nho Kyoho ch&iacute;nh l&agrave; hương vị &nbsp;đặc trưng của rượu Sake l&agrave;m say l&ograve;ng người.</p>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/Nho-grapes/nho-ruou-nhat-ban-1-min.jpg\" /></p>\r\n\r\n<p>- Nho rượu Kyoho được trồng trong nh&agrave; k&iacute;nh, mỗi ch&ugrave;m nho khi c&ograve;n non được bọc trong một &nbsp;lớp giấy trắng đặc biệt để tr&aacute;nh tuyệt đối c&aacute;c bụi bẩn v&agrave; c&ocirc;n tr&ugrave;ng.</p>\r\n\r\n<h3>3. Gi&aacute; trị dinh dưỡng</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Nho đen rượu Kyoho l&agrave; giống nho của Nhật cung cấp nhiều chất &nbsp;chống oxi &nbsp;h&oacute;a rất tốt cho tim mạch, huyết &aacute;p cao. Ch&uacute;ng cũng rất tốt &nbsp;cho mắt, n&atilde;o &nbsp;v&agrave; tr&iacute; nhớ.</li>\r\n	<li>Vỏ nho rất tốt cho sức khỏe như: tăng &nbsp;cường sức đề kh&aacute;ng, chống l&atilde;o &nbsp;h&oacute;a, c&oacute; t&aacute;c dụng thải độc tố&hellip; H&agrave;m lượng &nbsp;resveratrol trong vỏ nho phong &nbsp;ph&uacute; hơn trong thịt v&agrave; hạt nho, đồng thời &nbsp;c&oacute; t&aacute;c dụng giảm m&aacute;u nhiễm mỡ, &nbsp;chống tụ huyết, ph&ograve;ng chống xơ vữa động &nbsp;mạch v&agrave; tăng cường hệ thống miễn &nbsp;dịch của cơ thể.</li>\r\n	<li>Việc ăn nho &nbsp;thường xuy&ecirc;n gi&uacute;p cơ thể khỏe mạnh, gi&uacute;p nhanh phục hồi đối &nbsp;với những &nbsp;người bị thiếu m&aacute;u kinh ni&ecirc;n, những bệnh nh&acirc;n bị huyết &aacute;p &nbsp;cao, vi&ecirc;m phế &nbsp;quản, gout, vi&ecirc;m dạ d&agrave;y v&agrave; t&aacute;o b&oacute;n.</li>\r\n</ul>\r\n\r\n<h3>4. Lưu &yacute; bảo quản</h3>\r\n\r\n<p>Nho rượu Nhật Bản c&oacute; độ ngọt cao n&ecirc;n rất dễ bị rụng n&ecirc;n hạn chế chạm v&agrave;o quả.</p>\r\n\r\n<p>Bảo quản trong tủ m&aacute;t từ 3-8 độ C</p>\r\n', 'nho-ruou-nhat-ban-1_87.jpg', 'nho-ruou-nhat-ban-1.jpg', 1, 1, '2021-04-23 00:05:33'),
('000000000009', 23, 'Cherry Mỹ', 'cherry-my', 'Kg', 'Mỹ', '– Có hàm lượng vitamin A cao, không chỉ tốt cho mắt, ăn quả cherry Mỹ hàng ngày còn cải thiện làn da.\r\n– Cải thiện tình trạng mất trí nhớ, ngăn ngừa ung thư, làm chậm lão hóa.\r\n– Tốt cho tim mạch, giảm viêm nhiễm xương khớp, đau nhức cơ. Lượng chất xơ dồi', '<h1><strong>CHERRY MỸ</strong></h1>\r\n\r\n<h2>1.Giống v&agrave; chủng loại</h2>\r\n\r\n<p>-Nhập khẩu Mỹ</p>\r\n\r\n<p>M&ugrave;a cherry Mỹ thường k&eacute;o d&agrave;i 5 th&aacute;ng từ th&aacute;ng 5 đến hết th&aacute;ng 9 h&agrave;ng năm t&ugrave;y v&agrave;o điều kiện thời tiết.</p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>&ndash;&nbsp;<a href=\"http://hoaquafuji.com/news/cach-phan-biet-cherry-my-va-cherry-trung-quoc\">Quả cherry Mỹ</a>&nbsp;da l&aacute;ng b&oacute;ng, thịt chắc, m&agrave;u đỏ tươi, khi ch&iacute;n ngả m&agrave;u t&iacute;m t&iacute;a rất đẹp. Thịt cherry mọng, &oacute;ng ả như ngọc b&iacute;ch, ăn gi&ograve;n ngọt, đậm đ&agrave;, hương thơm dịu nhẹ thư th&aacute;i, hạt r&oacute;c kh&ocirc;ng d&iacute;nh.<br />\r\n&ndash; Cherry thường được d&ugrave;ng l&agrave;m qu&agrave; biếu sang trọng.</p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/cherry-my-1.jpg\" /></p>\r\n\r\n<p>&ndash; C&oacute; h&agrave;m lượng vitamin A cao, kh&ocirc;ng chỉ tốt cho mắt, ăn quả cherry Mỹ h&agrave;ng ng&agrave;y c&ograve;n cải thiện l&agrave;n da.<br />\r\n&ndash; Cải thiện t&igrave;nh trạng mất tr&iacute; nhớ, ngăn ngừa ung thư, l&agrave;m chậm l&atilde;o h&oacute;a.<br />\r\n&ndash; Tốt cho tim mạch, giảm vi&ecirc;m nhiễm xương khớp, đau nhức cơ. Lượng chất xơ dồi d&agrave;o trong cherry hỗ trợ tốt cho ti&ecirc;u h&oacute;a.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ndash; Mua cherry về bạn rửa sạch sau đ&oacute; để tr&aacute;i cherry trong t&igrave;nh trạng kh&ocirc; r&aacute;o, tr&aacute;nh nước đọng ở cuống bởi vỏ cherry rất mỏng n&ecirc;n n&oacute; hấp thụ nước rất nhanh khiến cherry bị ủng v&agrave; thối nhũn.</p>\r\n\r\n<p>-Sau khi để cherry kh&ocirc; r&aacute;o th&igrave; bạn cho ch&uacute;ng v&agrave;o t&uacute;i nilong buộc miệng hoặc hộp nhựa v&agrave; cho trong tủ lạnh ở nhiệt độ 0-4 độ C chắc chắn tr&aacute;i cherry giữ được độ tươi ngon đến 1 tuần</p>\r\n', 'cherrymy-221-3-755-755-1541993678_15.jpg', 'cherrymy-221-3-755-755-1541993678.jpg', 1, 1, '2021-04-23 00:08:44'),
('000000000010', 23, ' Cherry Chile', 'cherry-chile', '', 'Chile', '– Có hàm lượng vitamin A cao, không chỉ tốt cho mắt, ăn quả cherry Mỹ hàng ngày còn cải thiện làn da.\r\n\r\n– Cải thiện tình trạng mất trí nhớ, ngăn ngừa ung thư, làm chậm lão hóa.\r\n\r\n– Tốt cho tim mạch, giảm viêm nhiễm xương khớp, đau nhức cơ. Lượng chất xơ', '<h1><strong>CHERRY CHILE</strong></h1>\r\n\r\n<h2>1.Giống v&agrave; chủng loại</h2>\r\n\r\n<p>- Nhập khẩu Chile&nbsp;</p>\r\n\r\n<p>- Được trồng ở khu vực ph&iacute;a Bắc Santiago v&agrave; ph&iacute;a nam Curico của Chile. &nbsp;Đ&acirc;y l&agrave; khu vực c&oacute; kh&iacute; hậu m&aacute;t mẻ v&agrave;o m&ugrave;a h&egrave;, sương gi&aacute; v&agrave;o m&ugrave;a đ&ocirc;ng gi&uacute;p &nbsp;n&oacute; đơm hoa v&agrave; ph&aacute;t triển tốt nhất mang hương vị ngọt ng&agrave;o ri&ecirc;ng biệt.</p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>&ndash; Quả cherry Chile da l&aacute;ng b&oacute;ng, thịt chắc, m&agrave;u đỏ tươi, khi ch&iacute;n ngả m&agrave;u t&iacute;m t&iacute;a rất đẹp. &nbsp;Thịt cherry mọng, &oacute;ng ả như ngọc b&iacute;ch, ăn gi&ograve;n ngọt, đậm đ&agrave;, hương thơm &nbsp;dịu nhẹ thư th&aacute;i, hạt r&oacute;c kh&ocirc;ng d&iacute;nh.<br />\r\n&ndash; Cherry thường được d&ugrave;ng l&agrave;m qu&agrave; biếu sang trọng.</p>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/cherry/cherry-chile-1.jpg\" /></p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>&ndash; C&oacute; h&agrave;m lượng vitamin A cao, kh&ocirc;ng chỉ tốt cho mắt, ăn quả cherry Chile h&agrave;ng ng&agrave;y c&ograve;n cải thiện l&agrave;n da.<br />\r\n&ndash; Cải thiện t&igrave;nh trạng mất tr&iacute; nhớ, ngăn ngừa ung thư, l&agrave;m chậm l&atilde;o h&oacute;a.<br />\r\n&ndash; Tốt cho tim mạch, giảm vi&ecirc;m nhiễm xương khớp, đau nhức cơ. Lượng chất xơ dồi d&agrave;o trong cherry hỗ trợ tốt cho ti&ecirc;u h&oacute;a.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ndash; Mua cherry về bạn rửa sạch sau đ&oacute; để tr&aacute;i cherry trong t&igrave;nh trạng &nbsp;kh&ocirc; r&aacute;o, tr&aacute;nh nước đọng ở cuống bởi vỏ cherry rất mỏng n&ecirc;n n&oacute; hấp thụ &nbsp;nước rất nhanh khiến cherry bị ủng v&agrave; thối nhũn.</p>\r\n\r\n<p>-Sau khi để cherry kh&ocirc; r&aacute;o th&igrave; bạn cho ch&uacute;ng v&agrave;o t&uacute;i nilong buộc &nbsp;miệng hoặc hộp nhựa v&agrave; cho trong tủ lạnh ở nhiệt độ 0-4 độ C chắc chắn &nbsp;tr&aacute;i cherry giữ được độ tươi ngon đến 1 tuần</p>\r\n', 'cherry-chile-1_55.jpg', 'cherry-chile-1.jpg', 1, 1, '2021-04-23 00:10:08'),
('000000000011', 23, 'Cherry Vàng', 'cherry-vang', 'Kg', 'Mỹ', 'Cherry Vàng là nguồn vitamin A tuyệt vời, là loại trái cây giàu chất sắt, chất xơ cao, chất béo, không cholesterol, tốt cho hệ miễn dịch, tiêu hóa và làm đẹp da.', '<h1><strong>Cherry V&agrave;ng</strong></h1>\r\n\r\n<h2>1. Giống v&agrave; chủng loại</h2>\r\n\r\n<p>Cherry v&agrave;ng Mỹ được lai tạo bởi 2 giống cherry Bing &nbsp;v&agrave; Van tại chương tr&igrave;nh nh&acirc;n giống của Washington State University năm &nbsp;1952. Được ch&iacute;nh thức đưa ra thị trường v&agrave;o năm 1960.</p>\r\n\r\n<p>Cherry v&agrave;ng Mỹ<strong>&nbsp;</strong>l&agrave; loại hoa quả tươi ngon từ Mỹ l&agrave; ngon nhất bởi v&igrave; nơi đ&acirc;y c&oacute; kh&iacute; hậu th&iacute;ch hợp ph&ugrave; hợp cho &nbsp;cherry ph&aacute;t triển: nhiều nắng, nhạy cảm với gi&oacute; v&agrave; thay đổi nhiệt độ, &nbsp;mưa. Tuy rất kh&oacute; khăn để trồng nhưng Cherry lại l&agrave; c&acirc;y cho gi&aacute; trị cao.</p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>Cherry v&agrave;ng Mỹ c&oacute; m&agrave;u v&agrave;ng tươi b&oacute;ng bẩy, phần da căng mọng v&agrave; ửng &nbsp;hồng về phần cuống như m&aacute; của thiếu nữ tuổi đ&ocirc;i mươi, quả rất to v&agrave; chắc &nbsp;.</p>\r\n\r\n<p>Phần thịt cherry c&oacute; m&agrave;u v&agrave;ng kem, nhiều nước tạo l&ecirc;n độ &aacute;nh như ngọc, &nbsp;c&oacute; độ ngọt cao hơn c&aacute;c loại cherry kh&aacute;c, thịt chắc gi&ograve;n m&agrave; thanh ở &nbsp;cuống họng. C&oacute; đ&ocirc;i l&uacute;c bạn lầm tưởng như đang ăn vị caramel đặc biệt&nbsp;</p>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/cherry-vang-1.jpg\" /></p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>Cherry thường được v&iacute; như &nbsp;&ldquo;Kim cương của c&aacute;c loại &nbsp;hoa quả&rdquo; bởi gi&aacute; trị dinh dưỡng của n&oacute; mang lại. n&oacute; l&agrave; nguồn cung cấp &nbsp;Vitamin A, chất săt, chất xơ cao m&agrave; kh&ocirc;ng c&oacute; cholesterol. Bởi vậy:</p>\r\n\r\n<ul>\r\n	<li>Tốt cho hệ miễn dịch, ti&ecirc;u h&oacute;a, v&agrave; l&agrave; thực phẩm l&agrave;nh mạnh cho người tiểu đường</li>\r\n	<li>Tốt cho hệ tim mạch, chống lại bệnh ung thư</li>\r\n	<li>Hiệu quả cho việc giảm đau, giảm vi&ecirc;m với những người bị gout v&agrave; khớp</li>\r\n	<li>Gi&uacute;p bạn say giấc ngủ ngon</li>\r\n</ul>\r\n\r\n<h2>4. Lưu &yacute; bảo quản</h2>\r\n\r\n<ul>\r\n	<li>Lu&ocirc;n giữ lạnh quả Cherry v&igrave; quả Cherry rất mẫn cảm với nhiệt độ cao, chỉ cần để cherry trong 1 giờ ở nhiệt độ 20oC bạn sẽ thấy chất lượng quả cherry giảm đi r&otilde; rệt.</li>\r\n	<li>Lu&ocirc;n giữ Cherry ở t&igrave;nh trạng kh&ocirc; r&aacute;o, kh&ocirc;ng c&oacute; nước ở xung quanh vỏ &nbsp;hoặc cuống v&igrave; cherry sẽ hấp thu nước rất nhanh khuyến cherry sẽ mềm đi &nbsp;hoặc bị ủng.</li>\r\n	<li>Giữ cherry ở nhiệt độ chuẩn l&agrave; 0oC, độ ẩm từ 90% - 95% sẽ giữ được độ tươi trong nhiều ng&agrave;y liền (khoảng 10 ng&agrave;y)</li>\r\n	<li>Cherry cũng thường được ăn k&egrave;m với kem, pho m&aacute;t, b&aacute;nh ngọt, b&aacute;nh nướng, &hellip;&hellip;. Hoặc l&agrave;m mứt</li>\r\n</ul>\r\n', 'big_1403453615_82.jpg', 'big_1403453615.jpg', 1, 1, '2021-04-23 00:11:55'),
('000000000012', 18, 'Táo Xanh Mỹ', 'tao-xanh-my', 'Kg', 'Mỹ', 'Táo xanh Mỹ là một trong những thực phẩm tốt nhất cho sức khỏe tổng thể và sắc đẹp mà bạn nên biết và nên ăn hàng ngày:\r\n\r\n–  Trong một quả táo có chứa khoảng 4g chất xơ, đáp ứng được 17% nhu cầu  cần thiết của cơ thể mỗi ngày. Giúp điều trị bệnh tiểu đườ', '<h1><strong>T&Aacute;O XANH MỸ</strong></h1>\r\n\r\n<h2>1. Giống v&agrave; chủng loại</h2>\r\n\r\n<p>&ndash; T&aacute;o xanh Mỹ c&oacute; nguồn gốc từ trang trại ở Eastwood, New South Wasles, &Uacute;c năm 1868 v&agrave; ch&iacute;nh thức được c&ocirc;ng bố ra thị trường năm 1924. T&aacute;o xanh du nhập v&agrave; được trồng thương mại tại Mỹ bắt đầu từ cuối năm 1960<br />\r\n&ndash; T&aacute;o xanh Mỹ l&agrave; một trong những giống t&aacute;o được trồng phổ biến nhất ở tiểu bang Washington của Mỹ. Đ&acirc;y l&agrave; v&ugrave;ng đất m&agrave;u mỡ với kh&iacute; hậu kh&ocirc; n&oacute;ng quanh năm n&ecirc;n &iacute;t c&ocirc;n tr&ugrave;ng v&agrave; dịch bệnh, hệ thống tưới ti&ecirc;u thuận lợi do s&ocirc;ng Columbia v&agrave; v&ugrave;ng n&uacute;i Cascade cung cấp n&ecirc;n rất thuận lợi cho việc trồng t&aacute;o.<br />\r\n&ndash; T&aacute;o Xanh Mỹ c&oacute; vụ m&ugrave;a bắt đầu từ th&aacute;ng 1 đến th&aacute;ng 4 h&agrave;ng năm.</p>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/Tao-Apple/tao-xanh-my-2.jpg\" /></p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>&ndash; T&aacute;o c&oacute; vỏ ngo&agrave;i m&agrave;u xanh hoặc xanh hơi ngả sang v&agrave;ng. Thịt quả b&ecirc;n trong m&agrave;u trắng s&aacute;ng, độ gi&ograve;n vừa phải, c&oacute; vị r&ocirc;n rốt rất đặc trưng.<br />\r\n&ndash; Th&iacute;ch hợp l&agrave;m salad, &eacute;p nước hoặc nguy&ecirc;n liệu cho m&oacute;n b&aacute;nh tr&aacute;i c&acirc;y gi&agrave;u dinh dưỡng.</p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>T&aacute;o xanh Mỹ l&agrave; một trong những thực phẩm tốt nhất cho sức khỏe tổng thể v&agrave; sắc đẹp m&agrave; bạn n&ecirc;n biết v&agrave; n&ecirc;n ăn h&agrave;ng ng&agrave;y:</p>\r\n\r\n<p>&ndash; Trong một quả t&aacute;o c&oacute; chứa khoảng 4g chất xơ, đ&aacute;p ứng được 17% nhu cầu cần thiết của cơ thể mỗi ng&agrave;y. Gi&uacute;p điều trị bệnh tiểu đường, cải thiện chức năng của hệ ti&ecirc;u h&oacute;a,bảo vệ tim mạch, hỗ trợ giảm c&acirc;n hiệu quả</p>\r\n\r\n<p>&ndash; Lượng vitamin A, C, B1 dồi d&agrave;o tăng cường sức khỏe, chống lại c&aacute;c gốc tự do, ngăn ngừa ung thư v&agrave; l&agrave;m chậm qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a</p>\r\n\r\n<p>&ndash; H&agrave;m lượng kho&aacute;ng chất: kali, canxi, photpho, sắt,&hellip; dồi d&agrave;o tốt cho da, t&oacute;c, m&oacute;ng; giảm triệu chứng của c&aacute;c bệnh về xương khớp.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản</h2>\r\n\r\n<p>Để t&aacute;o kh&ocirc;ng bị th&acirc;m, ta cắt t&aacute;o nh&uacute;ng qua nước lạnh c&oacute; pha vai giọt nước cốt chanh hoặc nước muối lo&atilde;ng.<br />\r\n&ndash; Nhiệt độ bảo quản: Bảo quản tại nh&agrave;: Tủ lạnh từ 4 đến 8 độ C. T&aacute;o giữ được độ tươi, độ gi&ograve;n trong v&ograve;ng 4 tuần. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, độ PSI thấp hơn (t&aacute;o xốp hơn). Cần tr&aacute;nh để với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ nhiễm m&ugrave;i.</p>\r\n\r\n<h3>&ndash; LƯU &Yacute;:</h3>\r\n', 'tao-xanh-my-1_56.jpg', 'tao-xanh-my-1.jpg', 1, 1, '2021-04-23 00:13:36'),
('000100234324', 18, 'Táo Envy Mỹ', 'tao-envy-my', 'kg', 'Miền Nam- Việt Nam', '✳️Có thể bạn chưa biết!\r\n\r\n Táo Envy được trồng nhiều tại USA và New Zealand nhưng chất lượng táo  đến từ các nông trại của New Zealand luôn vượt trội, với đẳng cấp khác  biệt!', '<h1><strong>T&Aacute;O ENVY MỸ</strong></h1>\r\n\r\n<h2>1. Giống v&agrave; chủng loại</h2>\r\n\r\n<p>- Xuất xứ: được nhập khẩu từ c&aacute;c n&ocirc;ng trại ở Washington, USA</p>\r\n\r\n<p>- M&ugrave;a vụ: từ th&aacute;ng 12 đến khoảng th&aacute;ng 4 - th&aacute;ng 5, dao động theo thời tiết</p>\r\n\r\n<p><img src=\"https://hoaquafuji.com/storage/app/media/Tao-Apple/TAO-ENVY-MY1-min.jpg\" /></p>\r\n\r\n<h2>2. Đặc điểm sản phẩm</h2>\r\n\r\n<p>&ndash; &nbsp;T&aacute;o Envy c&oacute; nhiều k&iacute;ch thước kh&aacute;c nhau, d&aacute;ng quả tr&ograve;n, rất chắc v&agrave; &nbsp; &nbsp;nặng. Vỏ quả m&agrave;u đỏ ruby hoặc điểm th&ecirc;m c&aacute;c sọc v&agrave;ng tr&ocirc;ng v&ocirc; c&ugrave;ng sang &nbsp; &nbsp;trọng v&agrave; bắt mắt.<br />\r\n&ndash; L&agrave; một trong những d&ograve;ng t&aacute;o cao cấp nhất. Thịt t&aacute;o m&agrave;u v&agrave;ng kem, độ gi&ograve;n rất cao, vị ngọt đậm đ&agrave; v&agrave; c&oacute; m&ugrave;i thơm dịu nhẹ.</p>\r\n\r\n<h2>3. Gi&aacute; trị dinh dưỡng</h2>\r\n\r\n<p>&ndash; &nbsp;T&aacute;o kh&ocirc;ng chỉ chứa c&aacute;c chất dinh dưỡng cần thiết cho n&atilde;o như đường, &nbsp; &nbsp;vitamin, kho&aacute;ng chất m&agrave; quan trọng hơn l&agrave; chứa một lượng lớn nguy&ecirc;n tố &nbsp; &nbsp;kẽm. Kẽm l&agrave; nguy&ecirc;n tố kh&ocirc;ng thể thiếu đối với cơ thể, kẽm c&oacute; quan hệ &nbsp; mật &nbsp;thiết với acid nucleic, protein để l&agrave;m tăng khả năng ghi nhớ của &nbsp; n&atilde;o &nbsp;người. Mặt kh&aacute;c, kẽm c&ograve;n phối hợp với một số chất để tạo ra kh&aacute;ng &nbsp; thể, &nbsp;n&acirc;ng cao khả năng đề kh&aacute;ng của cơ thể. Ngo&agrave;i ra t&aacute;o c&ograve;n chứa c&aacute;c &nbsp; nguy&ecirc;n &nbsp;tố vi lượng kh&aacute;c như canxi, kali, sắt, phốtpho, c&aacute;c vitamin C, &nbsp; B1, B2.<br />\r\n&ndash; Lượng magi&ecirc; v&agrave; kali trong t&aacute;o gi&uacute;p điều chỉnh &aacute;p suất m&aacute;u &nbsp;v&agrave; giữ &nbsp;cho &nbsp;nhịp đập tim ở mức ổn định, chất chống &ocirc;xy ho&aacute; tự nhi&ecirc;n gi&uacute;p &nbsp;bảo &nbsp;vệ &nbsp;th&agrave;nh mạch m&aacute;u .<br />\r\n-Vỏ t&aacute;o gi&agrave;u chất xơ v&agrave; c&oacute; lợi cho hệ ti&ecirc;u h&oacute;a, hơn 1 nửa lượng vitamin C của quả t&aacute;o đều nằm ở vỏ.<br />\r\n&ndash; Đường tự nhi&ecirc;n trong t&aacute;o kh&ocirc;ng ảnh hưởng tới bệnh nh&acirc;n tiểu đường.<br />\r\n&ndash; Mặt kh&aacute;c, m&ugrave;i thơm đặc trưng của t&aacute;o c&oacute; t&aacute;c dụng giải tỏa sự căng thẳng, l&agrave;m cho tinh thần thoải m&aacute;i, vui vẻ.</p>\r\n\r\n<h2>4. Lưu &yacute; bảo quản</h2>\r\n\r\n<p>&ndash; Để t&aacute;o kh&ocirc;ng bị th&acirc;m, ta cắt t&aacute;o nh&uacute;ng qua nước lạnh c&oacute; pha vai giọt nước cốt chanh hoặc nước muối lo&atilde;ng.<br />\r\n&ndash; &nbsp;Nhiệt độ bảo quản: Bảo quản tại nh&agrave;: Tủ lạnh từ 4 đến 8 độ C. &nbsp;T&aacute;o giữ &nbsp; đươc độ tươi, độ gi&ograve;n trong v&ograve;ng 4 tuần. Sau thời gian n&agrave;y, &nbsp;t&aacute;o sẽ ngọt &nbsp; hơn, độ PSI thấp hơn (t&aacute;o xốp hơn). Cần tr&aacute;nh để với c&aacute;c &nbsp;thực phẩm c&oacute; &nbsp; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ nhiễm m&ugrave;i.<br />\r\n&ndash; LƯU &Yacute;:<br />\r\n+ T&aacute;o bị &nbsp;th&acirc;m b&ecirc;n trong: vỏ quả t&aacute;o rất khỏe, hơn nữa thường được &nbsp;tr&aacute;ng &nbsp;một lớp &nbsp;s&aacute;p ong trước khi xuất khẩu (từ Washington) n&ecirc;n rất &iacute;t &nbsp;khi hỏng &nbsp;từ b&ecirc;n &nbsp;ngo&agrave;i, trừ khi vỏ bị dập tr&ograve;ng qu&aacute; tr&igrave;nh vận chuyển.<br />\r\nCuống t&aacute;o l&agrave; nơi &nbsp;nhạy cảm nhất, thường chỉ c&oacute; một vết xước nhỏ ở &nbsp;cuống &nbsp;hoặc c&oacute; nước &nbsp;đọng cũng l&agrave;m vi khuẩn th&acirc;m nhập quả t&aacute;o v&agrave; l&agrave;m &nbsp;hỏng t&aacute;o từ &nbsp;b&ecirc;n trong.<br />\r\n+ &nbsp;T&aacute;o bị xốp: T&aacute;o bị xốp kh&ocirc;ng phải l&agrave; t&aacute;o hỏng m&agrave; do độ gi&ograve;n &nbsp;(PSI) bị &nbsp; giảm đi. Nhiều người lại th&iacute;ch ăn t&aacute;o xốp v&igrave; n&oacute; kh&ocirc;ng qu&aacute; &nbsp;cứng, nhất l&agrave; &nbsp; khi cho trẻ em ăn. T&aacute;o xốp thường rất ngọt. T&aacute;o xốp vẫn &nbsp;đảm bảo chất &nbsp; lượng v&agrave; h&agrave;m lượng vitamin trong quả. T&aacute;o xốp l&agrave; do nhiều &nbsp;nguy&ecirc;n nh&acirc;n:<br />\r\n* Thứ nhất: bản th&acirc;n t&aacute;o khi h&aacute;i đ&atilde; ch&iacute;n n&ecirc;n kể cả khi bảo quản lạnh đ&uacute;ng ti&ecirc;u chuẩn, n&oacute; vẫn ch&iacute;n v&agrave; trở n&ecirc;n xốp hơn.<br />\r\n* Thứ hai: lỗi bảo quản nhiệt độ kh&ocirc;ng đ&uacute;ng quy định l&agrave;m t&aacute;o nhanh ch&iacute;n hơn.</p>\r\n', 'IMG_0797-edit1-min_27.jpg', 'IMG_0797-edit1-min.jpg', 1, 1, '2021-04-10 23:07:06');

